$(document).ready(function () {
  console.log("ready!");

  $(".header__toggle").on("click", function (event) {
    event.preventDefault();
    $("body, html").toggleClass("scroll-fixed");
    $(".header__menu").toggle();
    $(".header__toggle").toggleClass("button-active");
  });

  const form = document.querySelector(".form");
  const formContainer = form.querySelector(".form__container");
  const formMessage = form.querySelector(".form__message");

  const showMessage = () => {
    form.classList.add("form--sended");
  };

  formContainer.addEventListener("submit", (evt) => {
    evt.preventDefault();
    showMessage();
  });

// init Swiper:
new Swiper('.slider', {
  slidesPerView: 1,
  spaceBetween: 0,
  loop: true,
  centerSlides: true,
  wrapperClass: 'slider__list',
  slideClass: 'slider__item',

  pagination: {
    el: '.slider__pagination',
    type: 'bullets',
    bulletClass: 'paginator__item',
    bulletActiveClass: 'paginator__item--active',
    clickable: true,
  },
  breakpoints: {
    768: {
      slidesPerView: 2,
      spaceBetween: 35,
    },
    1025: {
      slidesPerView: 3,
      spaceBetween: 30,
      navigation: {
        nextEl: '.slider__button--next',
        prevEl: '.slider__button--prev',
      },
    }
  }
});



;
});

function openForm() {
  $("body").addClass("stop-scrolling");
  $("#myForm").fadeIn(300).css("display", "flex");
}

function closeForm() {
  $("#myForm").fadeOut(200);
  $("body").removeClass("stop-scrolling");
  $(".form").removeClass("form--sended");
}
